import React from 'react';
import axios from 'axios';

export default class MovieList extends React.Component {
  state = {
    Search: [],
    totalResults:0,
    Response:false
  }

  componentDidMount() {
    axios.get(`http://www.omdbapi.com/?apikey=faf7e5bb&s=Batman&page=2`)
      .then(res => {
        this.setState({
            Search       :res.data.Search,
            totalResults :res.data.totalResults,
            Response     :res.data.Response
        });

      })
  }




  render() {
    return (
            <div>
                <h3>LIST MOVIE</h3>
                <h3>TOTAL RESULT : {this.state.totalResults} </h3>
                <h3>RESPONSE: {this.state.Response} </h3>
                <ul>{listMovies}</ul>
            </div>

    )
  }
}

const listMovies = this.state.Search.map((listMovie) =>
          <div key={listMovie.imdbID}>
              <h3>{listMovie.Title}</h3>
              <p>{listMovie.Year}</p>
              <p>{listMovie.Type}</p>
          </div>
);
